mapper.select:
SELECT

FROM
wx_slider
WHERE
status != -1

    AND status = #{status}


    AND slider_type = #{sliderType}


    AND slider_name LIKE CONCAT('%' , #{name,jdbcType=VARCHAR} , '%')

ORDER BY id DESC
LIMIT #{pageNo} , #{pageSize}

SELECT count(1) FROM wx_slider WHERE status != -1
AND status = #{status}
AND slider_type = #{sliderType}
AND slider_name LIKE CONCAT('%' , #{name,jdbcType=VARCHAR} , '%')
